#!/bin/sh

exec jackd -R -d alsa -d hw:CA0106

darrell@debdd:~$ cat .asoundrc

pcm.!default {
type plug
slave { pcm "jack" }
}

ctl.mixer0 {
type hw
card 0
}

# pcm type jack
pcm.jack {
type jack
playback_ports {
0 system:playback_1
1 system:playback_2
}
capture_ports {
0 system:capture_1
1 system:capture_2
}
}
